/*!40101 SET @saved_cs_client     = @@character_set_client */;
/*!40101 SET character_set_client = utf8 */;
CREATE TABLE `campaign` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `donation_request_id` int(11) DEFAULT NULL COMMENT 'campaign cho request nao',
  `name` varchar(1024) NOT NULL COMMENT 'title cua chien dich',
  `ascii_name` varchar(200) DEFAULT NULL COMMENT 'string khong dau cua name --> de search',
  `short_description` varchar(500) DEFAULT NULL,
  `thumbnail` varchar(200) DEFAULT NULL COMMENT 'danh sach cac images, json encoded\n',
  `campaign_code` varchar(64) DEFAULT 'CP',
  `type` smallint(6) NOT NULL DEFAULT '1' COMMENT '1 - tien\n2 - hien vat',
  `tags` varchar(500) DEFAULT NULL,
  `description` text,
  `content` text COMMENT 'HTML content',
  `view_count` int(11) NOT NULL DEFAULT '0',
  `like_count` int(11) NOT NULL DEFAULT '0',
  `comment_count` int(11) NOT NULL DEFAULT '0',
  `follower_count` int(11) NOT NULL DEFAULT '0',
  `status` int(11) NOT NULL DEFAULT '0' COMMENT '0 - new (chua duyet)\n9 - dang duyet\n10 - active\n1 - expired\n2 - inactive\n5 - rejected\n',
  `admin_note` varchar(4000) DEFAULT NULL COMMENT 'ly do reject...',
  `expected_amount` double NOT NULL DEFAULT '0',
  `current_amount` double NOT NULL DEFAULT '0',
  `donor_count` int(11) NOT NULL DEFAULT '0',
  `currency` varchar(20) DEFAULT 'VND',
  `honor` int(11) DEFAULT '0' COMMENT '0 --> nothing\n1 --> featured\n2 --> hot\n3 --> especial',
  `approved_at` int(11) DEFAULT NULL,
  `created_by` int(11) NOT NULL,
  `created_for_user` int(11) DEFAULT NULL,
  `created_at` int(11) DEFAULT NULL,
  `updated_at` int(11) DEFAULT NULL,
  `donation_status` smallint(6) NOT NULL DEFAULT '0' COMMENT '10 - gold reach',
  `started_at` int(11) DEFAULT NULL,
  `finished_at` int(11) DEFAULT NULL,
  `village_id` int(11) DEFAULT NULL,
  `lead_donor_id` int(11) DEFAULT NULL,
  `direct_donation_address` text,
  `screenshots` text,
  `published_at` int(11) DEFAULT NULL,
  PRIMARY KEY (`id`),
  UNIQUE KEY `code_UNIQUE` (`campaign_code`),
  KEY `idx_name` (`name`(255)),
  KEY `idx_tags` (`tags`(255)),
  KEY `idx_short_desc` (`short_description`(255)),
  KEY `idx_desc` (`description`(255)),
  KEY `idx_view_count` (`view_count`),
  KEY `idx_like_count` (`like_count`),
  KEY `idx_comment_count` (`comment_count`),
  KEY `idx_favorite_count` (`follower_count`),
  KEY `idx_is_deleted` (`status`),
  KEY `fk_campaign_user1_idx` (`created_by`),
  KEY `fk_campaign_user2_idx` (`created_for_user`),
  KEY `fk_campaign_donation_request1_idx` (`donation_request_id`),
  CONSTRAINT `fk_campaign_donation_request1` FOREIGN KEY (`donation_request_id`) REFERENCES `donation_request` (`id`) ON DELETE NO ACTION ON UPDATE NO ACTION,
  CONSTRAINT `fk_campaign_user1` FOREIGN KEY (`created_by`) REFERENCES `user` (`id`) ON DELETE NO ACTION ON UPDATE NO ACTION,
  CONSTRAINT `fk_campaign_user2` FOREIGN KEY (`created_for_user`) REFERENCES `user` (`id`) ON DELETE NO ACTION ON UPDATE NO ACTION
) ENGINE=InnoDB AUTO_INCREMENT=59 DEFAULT CHARSET=utf8 ROW_FORMAT=DYNAMIC COMMENT='TODO: thong tin ve cac thuoc tinh nhu dao dien, tac gia, ca ';
/*!40101 SET character_set_client = @saved_cs_client */;

--
-- Dumping data for table `campaign`
--

LOCK TABLES `campaign` WRITE;
/*!40000 ALTER TABLE `campaign` DISABLE KEYS */;
INSERT INTO `campaign` VALUES (20,NULL,'Xây dựng trường cho em',NULL,'Xây dựng trường học kiên cố, tạo điều kiện học tập tốt hơn cho gần 100 trẻ em người H’Mông ở bản Lao Lủng Tủng & Phe Phà (cách Lao Lủng Tủng 1km), xã Lũng Hồ, huyện Yên Minh, Hà Giang.','57bd9859a5f501472043097.jpg','CP000020',1,NULL,NULL,'<p style=\"margin: 0px; padding: 0px 0px 1em; border: 0px; outline: 0px; font-size: 15px; vertical-align: baseline; line-height: 1.7em; color: rgb(10, 10, 10); font-family: Raleway, Helvetica, Arial, Lucida, sans-serif; background-image: initial; background-attachment: initial; background-size: initial; background-origin: initial; background-clip: initial; background-position: initial; background-repeat: initial;\"><span style=\"color: rgb(10, 10, 10); font-family: Raleway, Helvetica, Arial, Lucida, sans-serif; font-size: 15px; line-height: 25.5px;\">X&atilde; Lũng Hồ thuộc huyện Y&ecirc;n Minh, H&agrave; Giang nằm c&aacute;ch th&agrave;nh phố H&agrave; Giang 60km, l&agrave; một x&atilde; c&ograve;n ngh&egrave;o, người d&acirc;n thưa thớt. X&atilde; nằm gọn trong l&ograve;ng n&uacute;i, trước đ&acirc;y 2 năm c&ograve;n kh&ocirc;ng c&oacute; được một con đường đ&aacute; răm để v&agrave;o bản. Điều kiện kinh tế nhiều kh&oacute; khăn khiến việc gi&aacute;o dục, đ&agrave;o tạo kh&ocirc;ng được ch&uacute; trọng.</span></p>\r\n\r\n<p style=\"margin: 0px; padding: 0px 0px 1em; border: 0px; outline: 0px; font-size: 15px; vertical-align: baseline; line-height: 1.7em; color: rgb(10, 10, 10); font-family: Raleway, Helvetica, Arial, Lucida, sans-serif; background-image: initial; background-attachment: initial; background-size: initial; background-origin: initial; background-clip: initial; background-position: initial; background-repeat: initial;\">Từ nhiều năm, c&aacute;c em học sinh chỉ học trong những căn ph&ograve;ng trống ho&aacute;c được lợp đơn giản bằng v&agrave;i tấm gỗ. Đ&acirc;y cũng l&agrave; nơi nghỉ của c&aacute;c gi&aacute;o vi&ecirc;n.</p>\r\n\r\n<div class=\"wp-caption aligncenter\" id=\"attachment_679\" style=\"margin: 0px auto 10px; padding: 8px; border: 1px solid rgb(221, 221, 221); outline: 0px; font-size: 14px; vertical-align: baseline; text-align: center; max-width: 96%; color: rgb(10, 10, 10); font-family: Raleway, Helvetica, Arial, Lucida, sans-serif; line-height: 23.8px; width: 611px; background: rgb(243, 243, 243);\"><img alt=\"Phòng học cho 28 học sinh mẫu giáo\" class=\"wp-image-679\" height=\"401\" src=\"http://i1.wp.com/xaytruongchoem.com/wp-content/uploads/2014/11/school1.jpg?resize=601%2C401\" style=\"margin: 0px; padding: 0px; border: 0px none; outline: 0px; vertical-align: baseline; max-width: 100%; height: auto; background: transparent;\" width=\"601\" />\r\n<p class=\"wp-caption-text\" style=\"margin: 0px; padding: 0px 4px; border: 0px; outline: 0px; font-size: 12px; vertical-align: baseline; line-height: 1.7em; background: transparent;\">Ph&ograve;ng học cho 28 học sinh mẫu gi&aacute;o</p>\r\n</div>\r\n\r\n<div class=\"wp-caption aligncenter\" id=\"attachment_680\" style=\"margin: 0px auto 10px; padding: 8px; border: 1px solid rgb(221, 221, 221); outline: 0px; font-size: 14px; vertical-align: baseline; text-align: center; max-width: 96%; color: rgb(10, 10, 10); font-family: Raleway, Helvetica, Arial, Lucida, sans-serif; line-height: 23.8px; width: 611px; background: rgb(243, 243, 243);\"><img alt=\"Phòng học lớp 3 ở Lao Lủng Tủng\" class=\"wp-image-680\" height=\"401\" src=\"http://i0.wp.com/xaytruongchoem.com/wp-content/uploads/2014/11/school2.jpg?resize=601%2C401\" style=\"margin: 0px; padding: 0px; border: 0px none; outline: 0px; vertical-align: baseline; max-width: 100%; height: auto; background: transparent;\" width=\"601\" />\r\n<p class=\"wp-caption-text\" style=\"margin: 0px; padding: 0px 4px; border: 0px; outline: 0px; font-size: 12px; vertical-align: baseline; line-height: 1.7em; background: transparent;\">Ph&ograve;ng học lớp 3 ở Lao Lủng Tủng</p>\r\n</div>\r\n\r\n<div class=\"wp-caption aligncenter\" id=\"attachment_681\" style=\"margin: 0px auto 10px; padding: 8px; border: 1px solid rgb(221, 221, 221); outline: 0px; font-size: 14px; vertical-align: baseline; text-align: center; max-width: 96%; color: rgb(10, 10, 10); font-family: Raleway, Helvetica, Arial, Lucida, sans-serif; line-height: 23.8px; width: 610px; background: rgb(243, 243, 243);\"><img alt=\"school3\" class=\"wp-image-681\" height=\"401\" src=\"http://i1.wp.com/xaytruongchoem.com/wp-content/uploads/2014/11/school3.jpg?resize=600%2C401\" style=\"margin: 0px; padding: 0px; border: 0px none; outline: 0px; vertical-align: baseline; max-width: 100%; height: auto; background: transparent;\" width=\"600\" />\r\n<p class=\"wp-caption-text\" style=\"margin: 0px; padding: 0px 4px; border: 0px; outline: 0px; font-size: 12px; vertical-align: baseline; line-height: 1.7em; background: transparent;\">Tường g&aacute; tạm bằng những v&aacute;n gỗ cũ, kh&ocirc;ng thể che k&iacute;n được gi&oacute; mưa</p>\r\n</div>\r\n\r\n<div class=\"wp-caption aligncenter\" id=\"attachment_682\" style=\"margin: 0px auto 10px; padding: 8px; border: 1px solid rgb(221, 221, 221); outline: 0px; font-size: 14px; vertical-align: baseline; text-align: center; max-width: 96%; color: rgb(10, 10, 10); font-family: Raleway, Helvetica, Arial, Lucida, sans-serif; line-height: 23.8px; width: 610px; background: rgb(243, 243, 243);\"><img alt=\"ĐIểm trường tiểu học nằm ở cuối bản, được dựng trên mảnh đất làm vườn do ông Páo hiến tặng\" class=\"wp-image-682\" height=\"401\" src=\"http://i0.wp.com/xaytruongchoem.com/wp-content/uploads/2014/11/school4.jpg?resize=600%2C401\" style=\"margin: 0px; padding: 0px; border: 0px none; outline: 0px; vertical-align: baseline; max-width: 100%; height: auto; background: transparent;\" width=\"600\" />\r\n<p class=\"wp-caption-text\" style=\"margin: 0px; padding: 0px 4px; border: 0px; outline: 0px; font-size: 12px; vertical-align: baseline; line-height: 1.7em; background: transparent;\">ĐIểm trường tiểu học nằm ở cuối bản, được dựng tr&ecirc;n mảnh đất l&agrave;m vườn do &ocirc;ng P&aacute;o hiến tặng</p>\r\n</div>\r\n\r\n<p style=\"margin: 0px; padding: 0px 0px 1em; border: 0px; outline: 0px; font-size: 15px; vertical-align: baseline; line-height: 1.7em; color: rgb(10, 10, 10); font-family: Raleway, Helvetica, Arial, Lucida, sans-serif; background-image: initial; background-attachment: initial; background-size: initial; background-origin: initial; background-clip: initial; background-position: initial; background-repeat: initial;\">Tuy nhi&ecirc;n, tại đ&acirc;y đ&atilde; c&oacute; nhiều người d&acirc;n &yacute; thức được sự quan trọng của gi&aacute;o dục. &Ocirc;ng P&aacute;o &ndash; một c&aacute;n bộ về hưu tại bản Lao Lủng Tủng đ&atilde; t&igrave;nh nguyện hiến tặng to&agrave;n bộ phần đất ruộng của m&igrave;nh để x&acirc;y điểm trường tại bản cho c&aacute;c em, để qu&atilde;ng đường 7km kh&ocirc;ng c&ograve;n l&agrave; trở ngại v&agrave; l&agrave; rủi ro cho c&aacute;c em nhỏ đến với con chữ. Với sự nhiệt t&igrave;nh của &ocirc;ng P&aacute;o v&agrave; c&aacute;n bộ địa phương, c&aacute;c gia đ&igrave;nh đ&atilde; g&oacute;p th&ecirc;m gỗ x&acirc;y tạm một lớp mẫu gi&aacute;o đơn sơ ở đầu bản v&agrave; một trường tiểu học nhỏ cuối bản.</p>\r\n\r\n<p style=\"margin: 0px; padding: 0px 0px 1em; border: 0px; outline: 0px; font-size: 15px; vertical-align: baseline; line-height: 1.7em; color: rgb(10, 10, 10); font-family: Raleway, Helvetica, Arial, Lucida, sans-serif; background-image: initial; background-attachment: initial; background-size: initial; background-origin: initial; background-clip: initial; background-position: initial; background-repeat: initial;\">Đến nay, &ocirc;ng P&aacute;o tiếp tục hiến tặng to&agrave;n bộ phần đất ruộng c&ograve;n lại của &ocirc;ng để c&oacute; đủ 160m2 x&acirc;y trường mới cho c&aacute;c ch&aacute;u.&nbsp;Điểm trường mới được x&acirc;y dựng tại bản Lao Lủng Tủng kh&ocirc;ng chỉ mang lại niềm vui cho c&aacute;c em tại bản, m&agrave; c&aacute;ch đ&oacute; 1km, c&aacute;c em học sinh ở bản Phe Ph&agrave; cũng được chung niềm vui.</p>\r\n\r\n<p style=\"margin: 0px; padding: 0px 0px 1em; border: 0px; outline: 0px; font-size: 15px; vertical-align: baseline; line-height: 1.7em; color: rgb(10, 10, 10); font-family: Raleway, Helvetica, Arial, Lucida, sans-serif; background-image: initial; background-attachment: initial; background-size: initial; background-origin: initial; background-clip: initial; background-position: initial; background-repeat: initial;\"><strong style=\"margin: 0px; padding: 0px; border: 0px; outline: 0px; vertical-align: baseline; background: transparent;\">Video clip dưới đ&acirc;y ghi lại những h&igrave;nh ảnh về đời sống v&agrave; hiện trạng trường học của c&aacute;c em:</strong></p>\r\n\r\n<p style=\"margin: 0px; padding: 0px 0px 1em; border: 0px; outline: 0px; font-size: 15px; vertical-align: baseline; line-height: 1.7em; color: rgb(10, 10, 10); font-family: Raleway, Helvetica, Arial, Lucida, sans-serif; background-image: initial; background-attachment: initial; background-size: initial; background-origin: initial; background-clip: initial; background-position: initial; background-repeat: initial;\">&nbsp;</p>\r\n\r\n<p style=\"margin: 0px; padding: 0px 0px 1em; border: 0px; outline: 0px; font-size: 15px; vertical-align: baseline; line-height: 1.7em; color: rgb(10, 10, 10); font-family: Raleway, Helvetica, Arial, Lucida, sans-serif; background-image: initial; background-attachment: initial; background-size: initial; background-origin: initial; background-clip: initial; background-position: initial; background-repeat: initial;\">&nbsp;</p>\r\n\r\n<div class=\"fluid-width-video-wrapper\" style=\"margin: 0px; padding: 446px 0px 0px; border: 0px; outline: 0px; font-size: 15px; vertical-align: baseline; width: 755px; position: relative; background: transparent;\"><iframe allowfullscreen=\"true\" class=\"youtube-player\" frameborder=\"0\" id=\"fitvid150967\" src=\"http://www.youtube.com/embed/9LEIglS9jHQ?version=3&amp;rel=1&amp;fs=1&amp;showsearch=0&amp;showinfo=1&amp;iv_load_policy=1&amp;wmode=transparent&amp;amp;wmode=opaque\" style=\"margin: 0px; padding: 0px; border-width: 0px; border-style: initial; outline: 0px; vertical-align: baseline; max-width: 100%; position: absolute; top: 0px; left: 0px; width: 755px; height: 446px; background: transparent;\" type=\"text/html\"></iframe></div>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p style=\"margin: 0px; padding: 0px 0px 1em; border: 0px; outline: 0px; font-size: 15px; vertical-align: baseline; line-height: 1.7em; color: rgb(10, 10, 10); font-family: Raleway, Helvetica, Arial, Lucida, sans-serif; background-image: initial; background-attachment: initial; background-size: initial; background-origin: initial; background-clip: initial; background-position: initial; background-repeat: initial;\">&nbsp;</p>\r\n\r\n<div class=\"su-box su-box-style-default\" style=\"margin: 0px 0px 1.5em; padding: 0px; outline: 0px; font-size: 14px; vertical-align: baseline; color: rgb(10, 10, 10); font-family: Raleway, Helvetica, Arial, Lucida, sans-serif; line-height: 23.8px; border-radius: 3px; border: 0px solid rgb(59, 95, 14) !important; background-image: initial; background-attachment: initial; background-size: initial; background-origin: initial; background-clip: initial; background-position: initial; background-repeat: initial;\">\r\n<div class=\"su-box-title\" style=\"margin: 0px; padding: 0.5em 1em; border: 0px; outline: 0px; font-size: 1.1em; vertical-align: baseline; font-weight: bold; color: rgb(255, 255, 255); border-top-left-radius: 2px; border-top-right-radius: 2px; background: rgb(74, 119, 17);\">Phong tr&agrave;o học tập tại địa phương</div>\r\n\r\n<div class=\"su-box-content su-clearfix\" style=\"margin: 0px; padding: 1em; border: 0px; outline: 0px; vertical-align: baseline; color: rgb(68, 68, 68); background-image: initial; background-attachment: initial; background-color: rgb(235, 235, 235) !important; background-size: initial; background-origin: initial; background-clip: initial; background-position: initial; background-repeat: initial;\">\r\n<p style=\"margin: 0px; padding: 0px 0px 1em; border: 0px; outline: 0px; font-size: 15px; vertical-align: baseline; line-height: 1.7em; background: transparent;\">Theo b&aacute;o c&aacute;o của UNICEF, chỉ t&iacute;nh ri&ecirc;ng đối tượng trẻ em dưới 5 tuổi, c&oacute; tới&nbsp;<strong style=\"margin: 0px; padding: 0px; border: 0px; outline: 0px; vertical-align: baseline; background: transparent;\">34,19%</strong>&nbsp;trẻ em người H&rsquo;Mong dưới 5 tuổi chưa bao giờ đến trường. Thế nhưng, ở x&atilde; Lũng Hồ, t&iacute;nh đến th&aacute;ng 9/2014, x&atilde; đ&atilde; huy động được 76/164 ch&aacute;u từ 0 &ndash; 2 tuổi (<strong style=\"margin: 0px; padding: 0px; border: 0px; outline: 0px; vertical-align: baseline; background: transparent;\">50,05%</strong>), 686/730 ch&aacute;u từ 3 &ndash; 5 tuổi (<strong style=\"margin: 0px; padding: 0px; border: 0px; outline: 0px; vertical-align: baseline; background: transparent;\">93%</strong>) đi học.</p>\r\n\r\n<p style=\"margin: 0px; padding: 0px; border: 0px; outline: 0px; font-size: 15px; vertical-align: baseline; line-height: 1.7em; background: transparent;\">C&oacute; thể n&oacute;i, x&atilde; Lũng Hồ l&agrave; một trong những địa b&agrave;n năng động v&agrave; t&iacute;ch cực nhất trong việc khuyến kh&iacute;ch gi&aacute;o dục đối với đối tượng trẻ em d&acirc;n tộc thiểu số.</p>\r\n</div>\r\n</div>\r\n\r\n<p style=\"margin: 0px; padding: 0px 0px 1em; border: 0px; outline: 0px; font-size: 15px; vertical-align: baseline; line-height: 1.7em; color: rgb(10, 10, 10); font-family: Raleway, Helvetica, Arial, Lucida, sans-serif; background-image: initial; background-attachment: initial; background-size: initial; background-origin: initial; background-clip: initial; background-position: initial; background-repeat: initial;\"><img alt=\"Học sinh Lũng Hồ, Hà Giang\" class=\"aligncenter size-full wp-image-50\" height=\"465\" src=\"http://i2.wp.com/xaytruongchoem.com/wp-content/uploads/2014/10/resized-_DSC0115.jpg?resize=700%2C465\" style=\"margin: 0px auto; padding: 0px; border: 0px; outline: 0px; vertical-align: baseline; max-width: 100%; height: auto; display: block; background: transparent;\" width=\"700\" /></p>\r\n',0,0,0,0,10,NULL,0,0,0,'VND',0,NULL,16,NULL,1472043097,1472518243,0,1470013248,1504173648,12,1,NULL,NULL,1472173396),(21,NULL,'Ấm áp 1.000 nụ cười trẻ em vùng cao',NULL,'Trong cái lạnh hơn chục độ sớm những ngày cuối tháng 10, hơn 1.000 học sinh và 100 giáo viên của 3 trường mầm non, tiểu học, trung học cơ sở trên địa bàn huyện Lũng Cú, Hà Giang đã được tặng áo ấm, thuốc bổ, sách… mở đầu cho chương trình “Giữ mãi nụ cười trẻ em” của năm.','57bdcf06d3e361472057094.jpg','CP000021',1,NULL,NULL,'<p style=\"margin: 9.1875px 0px; padding: 0px; outline: 0px; max-width: 100%; font-family: &quot;Times New Roman&quot;; font-size: 16px; line-height: 20px; color: rgb(0, 0, 0);\">Cụ thể, chương tr&igrave;nh &ldquo;Giữ m&atilde;i nụ cười trẻ em v&ugrave;ng cao&rdquo; do C&ocirc;ng ty Dược Hậu Giang phối hợp với ch&iacute;nh quyền địa phương huyện Lũng C&uacute; tổ chức ng&agrave;y 25/10 tại trường THCS Lũng Cũ đ&atilde; trao tặng 1 ph&ograve;ng học, 1.000 &aacute;o ấm cho học sinh, 100 phần qu&agrave; cho gi&aacute;o vi&ecirc;n c&aacute;c trường Trung học cơ sở v&agrave; Tiểu học tr&ecirc;n địa b&agrave;n huyện Lũng C&uacute;, H&agrave; Giang.</p>\r\n\r\n<p style=\"margin: 9.1875px 0px; padding: 0px; outline: 0px; max-width: 100%; font-family: &quot;Times New Roman&quot;; font-size: 16px; line-height: 20px; color: rgb(0, 0, 0);\">&nbsp;</p>\r\n\r\n<p style=\"margin: 9.1875px 0px; padding: 0px; outline: 0px; max-width: 100%; font-family: &quot;Times New Roman&quot;; font-size: 16px; line-height: 20px; color: rgb(0, 0, 0); text-align: center;\">&nbsp;</p>\r\n\r\n<div align=\"center\" style=\"margin: 0px; padding: 0px; outline: 0px; max-width: 100%; color: rgb(0, 0, 0); font-family: &quot;Times New Roman&quot;; font-size: 16px; line-height: 20px;\"><img _fl=\"\" align=\"center\" alt=\"Ấm áp 1.000 nụ cười trẻ em vùng cao\" src=\"http://dantri4.vcmedia.vn/exqBRdkpgE5xheGrhZovbKcha1K3Jt/Image/2014/10/lung-cu-291014-a55cd.jpg\" style=\"margin: 5px; padding: 0px; outline: 0px; display: inline; max-width: 460px; border: 0px;\" width=\"450\" /></div>\r\n\r\n<div align=\"center\" style=\"margin: 0px; padding: 0px; outline: 0px; max-width: 100%; color: rgb(0, 0, 0); font-family: &quot;Times New Roman&quot;; font-size: 16px; line-height: 20px;\"><span style=\"margin: 0px; padding: 0px; outline: 0px; max-width: 100%; font-family: Tahoma; font-size: 10pt;\">C&ocirc; Phạm Thị Tuyển (đứng giữa) c&ugrave;ng chị Nguyễn Thị Thanh Thu&yacute; (ngo&agrave;i c&ugrave;ng b&ecirc;n phải), đại diện Ban tổ chức, trao &aacute;o ấm cho học sinh trung học cơ sở Lũng C&uacute;</span></div>\r\n\r\n<p style=\"margin: 9.1875px 0px; padding: 0px; outline: 0px; max-width: 100%; font-family: &quot;Times New Roman&quot;; font-size: 16px; line-height: 20px; color: rgb(0, 0, 0);\">&nbsp;</p>\r\n\r\n<p style=\"margin: 9.1875px 0px; padding: 0px; outline: 0px; max-width: 100%; font-family: &quot;Times New Roman&quot;; font-size: 16px; line-height: 20px; color: rgb(0, 0, 0);\">Chia sẻ về những kh&oacute; khăn của học sinh v&ugrave;ng cao, c&ocirc; Phạm Thị Tuyển, hiệu trưởng trường THCS Lũng C&uacute;, cho biết: &ldquo;D&ugrave; r&eacute;t hơn 10 độ hay xuống gần 0 độ, c&aacute;c em học sinh vẫn chỉ c&oacute; bộ trang phục như vậy. Ch&uacute;ng t&ocirc;i phải tổ chức đốt lửa, tr&iacute;ch lương mua đường, đun nước đường gừng cho học sinh uống để giữ ấm cơ thể. V&igrave; thế những chiếc &aacute;o ấm n&agrave;y rất qu&yacute; gi&aacute;, sẽ gi&uacute;p c&aacute;c em c&oacute; th&ecirc;m sức khoẻ để học tập trong m&ugrave;a đ&ocirc;ng n&agrave;y&rdquo;.</p>\r\n\r\n<p style=\"margin: 9.1875px 0px; padding: 0px; outline: 0px; max-width: 100%; font-family: &quot;Times New Roman&quot;; font-size: 16px; line-height: 20px; color: rgb(0, 0, 0);\">&nbsp;</p>\r\n\r\n<p style=\"margin: 9.1875px 0px; padding: 0px; outline: 0px; max-width: 100%; font-family: &quot;Times New Roman&quot;; font-size: 16px; line-height: 20px; color: rgb(0, 0, 0);\">Ngo&agrave;i ra, Ban tổ chức cũng tặng tr&ecirc;n 600 đầu s&aacute;ch cho thư viện v&agrave; tổ chức c&aacute;c hoạt động vui chơi tại c&aacute;c trường để n&acirc;ng cao tri thức, tạo th&ecirc;m niềm vui, gi&uacute;p c&aacute;c em h&agrave;o hứng đến trường.</p>\r\n\r\n<p style=\"margin: 9.1875px 0px; padding: 0px; outline: 0px; max-width: 100%; font-family: &quot;Times New Roman&quot;; font-size: 16px; line-height: 20px; color: rgb(0, 0, 0); text-align: center;\">&nbsp;</p>\r\n\r\n<div align=\"center\" style=\"margin: 0px; padding: 0px; outline: 0px; max-width: 100%; color: rgb(0, 0, 0); font-family: &quot;Times New Roman&quot;; font-size: 16px; line-height: 20px;\"><img _fl=\"\" align=\"center\" alt=\"Ấm áp 1.000 nụ cười trẻ em vùng cao\" src=\"http://dantri4.vcmedia.vn/exqBRdkpgE5xheGrhZovbKcha1K3Jt/Image/2014/10/kham-benh-291014-61137.jpg\" style=\"margin: 5px; padding: 0px; outline: 0px; display: inline; max-width: 460px; border: 0px;\" width=\"450\" /></div>\r\n\r\n<div align=\"center\" style=\"margin: 0px; padding: 0px; outline: 0px; max-width: 100%; color: rgb(0, 0, 0); font-family: &quot;Times New Roman&quot;; font-size: 16px; line-height: 20px;\"><span style=\"margin: 0px; padding: 0px; outline: 0px; max-width: 100%; font-family: Tahoma; font-size: 10pt;\">C&aacute;c b&aacute;c sĩ kh&aacute;m bệnh cho b&agrave; con v&ugrave;ng cao</span></div>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p style=\"margin: 9.1875px 0px; padding: 0px; outline: 0px; max-width: 100%; font-family: &quot;Times New Roman&quot;; font-size: 16px; line-height: 20px; color: rgb(0, 0, 0);\">&nbsp;</p>\r\n\r\n<p style=\"margin: 9.1875px 0px; padding: 0px; outline: 0px; max-width: 100%; font-family: &quot;Times New Roman&quot;; font-size: 16px; line-height: 20px; color: rgb(0, 0, 0);\">&nbsp;</p>\r\n\r\n<p style=\"margin: 9.1875px 0px; padding: 0px; outline: 0px; max-width: 100%; font-family: &quot;Times New Roman&quot;; font-size: 16px; line-height: 20px; color: rgb(0, 0, 0);\">H&ocirc;m qua (28/10), cũng trong khu&ocirc;n khổ chương tr&igrave;nh, C&ocirc;ng ty Dược Hậu Giang đ&atilde; phối hợp c&ugrave;ng c&aacute;c b&aacute;c sĩ bệnh viện Lao &ndash; Phổi H&agrave; Giang kh&aacute;m bệnh ph&aacute;t thuốc miễn ph&iacute; cho 450 người d&acirc;n ở 3 x&atilde;: Lũng Ph&igrave;n, S&uacute;ng Tr&aacute;i, Hố Qu&aacute;ng Ph&igrave;n. Theo đ&oacute;, mỗi người d&acirc;n kh&aacute;m bệnh, si&ecirc;u &acirc;m sẽ được ph&aacute;t thuốc trị gi&aacute; 100.000đồng/suất. Những trường hợp ph&aacute;t hiện bệnh sẽ được giới thiệu l&ecirc;n bệnh viện tuyến tr&ecirc;n để điều trị.</p>\r\n\r\n<p style=\"margin: 9.1875px 0px; padding: 0px; outline: 0px; max-width: 100%; font-family: &quot;Times New Roman&quot;; font-size: 16px; line-height: 20px; color: rgb(0, 0, 0);\">&nbsp;</p>\r\n\r\n<p style=\"margin: 9.1875px 0px; padding: 0px; outline: 0px; max-width: 100%; font-family: &quot;Times New Roman&quot;; font-size: 16px; line-height: 20px; color: rgb(0, 0, 0);\">Hoạt động x&acirc;y ph&ograve;ng học, tặng &aacute;o ấm cho học sinh, gi&aacute;o vi&ecirc;n l&agrave; một n&eacute;t mới trong c&aacute;c hoạt động cộng đồng của c&ocirc;ng ty sau khi đ&atilde; tổ chức chương tr&igrave;nh Kh&aacute;m bệnh ph&aacute;t thuốc miễn ph&iacute; cho gần 89.000 lượt người d&acirc;n ngh&egrave;o tại c&aacute;c tỉnh v&ugrave;ng s&acirc;u, v&ugrave;ng xa tr&ecirc;n cả nước v&agrave; 1 số tỉnh ở Campuchia, L&agrave;o với chi ph&iacute; tr&ecirc;n 6,6 tỷ đồng c&ugrave;ng nhiều hoạt động kh&aacute;c với tổng chi ph&iacute; l&ecirc;n tới 12 tỉ đồng kể từ năm 2013 đến nay.</p>\r\n',0,0,0,0,10,NULL,0,0,0,'VND',0,NULL,16,NULL,1472057094,1474876214,0,1471657256,1475229656,1,7,NULL,NULL,1472057358),(22,20,'Thiếu 60 triệu đồng, thiếu nữ 24 tuổi nguy kịch vì bệnh tim',NULL,'Có tất cả 1,5 triệu đồng mang lên anh đã đóng hết vào viện phí với hi vọng con gái sẽ được mổ tim. Tuy nhiên chi phí được báo hết khoảng 60 triệu cho 2 ca mổ khiến anh ngã ngửa, bàng hoàng. Không vay được ai số tiền đóng, hàng ngày ở viện 2 bố con như ngồi trên đống lửa, hết đi ra, đi vào rồi lại nhìn nhau bật khóc.','57bf399ec5dc11472149918.jpg','CP000022',1,NULL,NULL,'<p style=\"margin: 9.1875px 0px; padding: 0px; outline: 0px; max-width: 100%; font-family: &quot;Times New Roman&quot;; font-size: 16px; line-height: 20px; color: rgb(0, 0, 0);\">&ldquo;Hai bố con xuống từ h&ocirc;m 9/8, b&aacute;c sĩ bảo nhập viện lu&ocirc;n v&igrave; bệnh n&oacute; nặng qu&aacute; rồi. H&ocirc;m nay th&igrave; n&oacute; c&oacute; lịch mổ nhưng gia đ&igrave;nh phải xin ho&atilde;n lại v&igrave; chưa hỏi được tiền để đ&oacute;ng v&agrave;o viện&rdquo;- Anh T&aacute;m bộc bạch bằng giọng buồn thiu pha ch&uacute;t sợ h&atilde;i.</p>\r\n\r\n<p style=\"margin: 9.1875px 0px; padding: 0px; outline: 0px; max-width: 100%; font-family: &quot;Times New Roman&quot;; font-size: 16px; line-height: 20px; color: rgb(0, 0, 0);\">Ngồi gần bố l&agrave; con g&aacute;i B&ugrave;i Thị Thu Hương năm nay đ&atilde; 24 tuổi nhưng v&igrave; mắc bệnh từ nhỏ th&agrave;nh ra tr&ocirc;ng em nhỏ th&oacute; như một học sinh cấp 2. Kh&oacute; thở v&agrave; kh&ocirc;ng ăn được, Hương bảo mệt đ&atilde; từ l&acirc;u nhưng đợt n&agrave;y hai bố con mới quyết t&acirc;m l&ecirc;n thủ đ&ocirc; để chữa trị. H&ocirc;m l&ecirc;n viện, số tiền gom g&oacute;p ở nh&agrave; v&agrave; đi vay bố con mang tất cả 1,7 triệu đồng th&igrave; đi lại mất 200.000 đồng, c&ograve;n 1,5 triệu đồng bố đ&atilde; đ&oacute;ng hết viện ph&iacute; nhưng b&aacute;c sĩ cho biết em phải mổ đến 2 lần với tổng chi ph&iacute; l&agrave; hơn 60 triệu đồng.</p>\r\n',0,0,0,0,10,NULL,0,0,0,'VND',0,NULL,16,NULL,1472149918,1474876187,0,1470795322,1472640622,1,7,NULL,NULL,1472150023),(23,21,'Mẹ chết, cha bệnh hiểm nghèo, bé 8 tuổi mơ ước được cùng cha ăn một miếng thịt',NULL,'Từ khi mẹ chết, bố đổ bệnh, bé Thuyết bỗng dưng trở thành trụ cột gia đình. Ngoài việc đi xin cơm, tự hái đu đủ xanh, lá rau cằn cỗi quanh nhà luộc để hai cha con ăn cho no bụng thì hơn 3 tháng qua, đứa trẻ này chưa một lần biết đến mùi vị của miếng thịt.','57bf90b979d471472172217.jpg','CP000023',1,NULL,NULL,'<p style=\"margin: 9.1875px 0px; padding: 0px; outline: 0px; max-width: 100%; font-family: &quot;Times New Roman&quot;; font-size: 16px; line-height: 20px; color: rgb(0, 0, 0);\">Cảnh đời cơ cực đ&oacute; đang diễn ra h&agrave;ng ng&agrave;y với c&ocirc; b&eacute; người d&acirc;n tộc K&rsquo;ho Ka L&acirc;m Thuyết (8 tuổi) c&ugrave;ng cha m&igrave;nh l&agrave; anh Ka Thơ</p>\r\n\r\n<p style=\"margin: 9.1875px 0px; padding: 0px; outline: 0px; max-width: 100%; font-family: &quot;Times New Roman&quot;; font-size: 16px; line-height: 20px; color: rgb(0, 0, 0);\">Tấn bị kịch ập đến với Ka L&acirc;m Thuyết từ 3 năm trước khi mẹ ruột của Thuyết tự tử v&igrave; chuyện gia đ&igrave;nh, theo phong tục &ldquo;bắt chồng&rdquo; của người K&rsquo;ho, khi mẹ chết, Ka L&acirc;m Thuyết phải theo cha về sống nhờ nh&agrave; &ocirc;ng nội. H&agrave;ng ng&agrave;y anh Ka Thơ l&agrave;m thu&ecirc; khắp nơi, ai thu&ecirc; g&igrave; l&agrave;m đ&oacute;, cuộc sống d&ugrave; vất vả nhưng hai cha con cũng đủ miếng cơm ăn qua ng&agrave;y.</p>\r\n\r\n<p style=\"margin: 9.1875px 0px; padding: 0px; outline: 0px; max-width: 100%; font-family: &quot;Times New Roman&quot;; font-size: 16px; line-height: 20px; color: rgb(0, 0, 0);\">Tưởng chừng cuộc sống &ecirc;m đềm tr&ocirc;i với c&ocirc; b&eacute; nhưng bất ngờ khoảng 3 th&aacute;ng trước, anh Ka Thơ đổ bệnh, bụng ph&igrave;nh to như c&aacute;i trống kh&ocirc;ng thể đi lại. Theo chẩn đo&aacute;n ban đầu của b&aacute;c sĩ, anh Ka Thơ bị bệnh xơ gan. Từ ng&agrave;y anh Ka Thơ nằm một chỗ, bỗng dưng Ka L&acirc;m Thuyết trở th&agrave;nh trụ cột gia đ&igrave;nh, h&agrave;ng ng&agrave;y Thuyết phải chăm cha, đi xin cơm b&agrave; con h&agrave;ng x&oacute;m, h&aacute;i đu đủ xanh, rau hoang cằn cỗi mọc quanh nh&agrave; để hai cha con ăn cho no bụng. Gia cảnh khốn kh&oacute;, miếng ăn c&ograve;n chưa trọn vẹn n&ecirc;n anh Ka Thơ cũng chẳng biết đến một vi&ecirc;n thuốc, bụng anh mỗi ng&agrave;y một to, ch&acirc;n tay teo t&oacute;p, hơi thở yếu dần.</p>\r\n\r\n<div class=\"VCSortableInPreviewMode\" style=\"margin: 0px; padding: 0px; outline: 0px; max-width: 100%; font-family: tahoma; color: rgb(0, 0, 0); line-height: 20px; display: inline-block; width: 460px; text-align: center; box-sizing: border-box; background-color: rgb(238, 238, 238);\" type=\"Photo\">\r\n<div style=\"margin: 0px auto; padding: 0px; outline: 0px; max-width: 100%;\"><img alt=\"\" id=\"img_292977\" photoid=\"292977\" rel=\"https://dantri4.vcmedia.vn/96a1faf49c/2016/03/27/5-1459082271470.jpg\" src=\"https://dantri4.vcmedia.vn/k:96a1faf49c/2016/03/27/5-1459082271470/me-chet-cha-benh-hiem-ngheo-be-8-tuoi-mo-uoc-duoc-cung-cha-an-mot-mieng-thit.jpg\" style=\"margin: 0px auto; padding: 0px; outline: 0px; display: inline; max-width: 100%; border: 0px;\" type=\"photo\" /></div>\r\n\r\n<div class=\"PhotoCMS_Caption\" style=\"margin: 0px auto; padding: 5px; outline: 0px; max-width: 100%;\">&nbsp;</div>\r\n</div>\r\n\r\n<div class=\"VCSortableInPreviewMode\" style=\"margin: 0px; padding: 0px; outline: 0px; max-width: 100%; font-family: tahoma; color: rgb(0, 0, 0); line-height: 20px; display: inline-block; width: 460px; text-align: center; box-sizing: border-box; background-color: rgb(238, 238, 238);\" type=\"Photo\">\r\n<div style=\"margin: 0px auto; padding: 0px; outline: 0px; max-width: 100%;\"><img alt=\"Mẹ mất sớm, cha đổ bệnh, Ka Lâm Thuyết trở thành trụ cột gia đình\" id=\"img_292978\" photoid=\"292978\" rel=\"https://dantri4.vcmedia.vn/96a1faf49c/2016/03/27/2-1459082271423.jpg\" src=\"https://dantri4.vcmedia.vn/k:96a1faf49c/2016/03/27/2-1459082271423/me-chet-cha-benh-hiem-ngheo-be-8-tuoi-mo-uoc-duoc-cung-cha-an-mot-mieng-thit.jpg\" style=\"margin: 0px auto; padding: 0px; outline: 0px; display: inline; max-width: 100%; border: 0px;\" type=\"photo\" /></div>\r\n\r\n<div class=\"PhotoCMS_Caption\" style=\"margin: 0px auto; padding: 5px; outline: 0px; max-width: 100%;\">Mẹ mất sớm, cha đổ bệnh, Ka L&acirc;m Thuyết trở th&agrave;nh trụ cột gia đ&igrave;nh</div>\r\n</div>\r\n\r\n<p style=\"margin: 9.1875px 0px; padding: 0px; outline: 0px; max-width: 100%; font-family: &quot;Times New Roman&quot;; font-size: 16px; line-height: 20px; color: rgb(0, 0, 0);\">Khi ch&uacute;ng t&ocirc;i hỏi &ldquo;Con th&iacute;ch g&igrave; nhất?&rdquo;, kh&ocirc;ng cần suy nghĩ, Ka L&acirc;m Thuyết đ&aacute;p ngay: &ldquo;Con th&iacute;ch ăn thịt&rdquo;. Nằm tr&ecirc;n chiếc giường ọp ẹp, bừa bộn, anh Ka Thơ cố lấy hơi giải th&iacute;ch: &ldquo;Hơn 3 th&aacute;ng rồi, n&oacute; chưa được ăn miếng thịt n&agrave;o, may mắn lắm th&igrave; được c&aacute;i đầu c&aacute; kh&ocirc;. Mấy lần n&oacute; đ&ograve;i ăn thịt, t&ocirc;i chỉ biết n&oacute;i với n&oacute; l&agrave; kh&ocirc;ng c&oacute; thịt đ&acirc;u m&agrave; ăn, cha đau kh&ocirc;ng đi l&agrave;m thu&ecirc; được l&agrave;m sao c&oacute; tiền m&agrave; mua thịt. Con m&agrave; đ&ograve;i ăn thịt nữa l&agrave; c&aacute;i bụng của cha n&oacute; vỡ ra, cha chết đ&oacute;! nghe t&ocirc;i n&oacute;i vậy th&igrave; con b&eacute; kh&ocirc;ng d&aacute;m đ&ograve;i nữa&rdquo;.</p>\r\n\r\n<p style=\"margin: 9.1875px 0px; padding: 0px; outline: 0px; max-width: 100%; font-family: &quot;Times New Roman&quot;; font-size: 16px; line-height: 20px; color: rgb(0, 0, 0);\">Nhiều th&aacute;ng sống trong cảnh thiếu thốn đủ bề, miếng ăn kh&ocirc;ng được no đ&atilde; khiến b&eacute; g&aacute;i 8 tuổi Ka L&acirc;m Thuyết đen sạm, nhỏ th&oacute; như đứa trẻ l&ecirc;n 4. Khi m&agrave;n đ&ecirc;m bu&ocirc;ng xuống, hai cha con Ka L&acirc;m Thuyết nằm co ro trong c&aacute;i lạnh buốt của tiết trời, c&oacute; khi cha l&ecirc;n cơn đau dữ dội, c&ocirc; b&eacute; 8 tuổi lại trở th&agrave;nh &ldquo;y t&aacute;&rdquo;, đ&ocirc;i b&agrave;n tay nhỏ thoăn thoắt b&oacute;p tay, b&oacute;p ch&acirc;n, xoa l&ecirc;n c&aacute;i bụng căng như trống của cha m&igrave;nh. &ldquo;Con rất sợ cha chết, cha chết kh&ocirc;ng ai ở với con&rdquo; &ndash; Ka L&acirc;m Thuyết run run n&oacute;i.</p>\r\n\r\n<div class=\"VCSortableInPreviewMode\" style=\"margin: 0px; padding: 0px; outline: 0px; max-width: 100%; font-family: tahoma; color: rgb(0, 0, 0); line-height: 20px; display: inline-block; width: 460px; text-align: center; box-sizing: border-box; background-color: rgb(238, 238, 238);\" type=\"Photo\">\r\n<div style=\"margin: 0px auto; padding: 0px; outline: 0px; max-width: 100%;\"><img alt=\"\" id=\"img_292979\" photoid=\"292979\" rel=\"https://dantri4.vcmedia.vn/96a1faf49c/2016/03/27/hinh5-1459082271293.jpg\" src=\"https://dantri4.vcmedia.vn/k:96a1faf49c/2016/03/27/hinh5-1459082271293/me-chet-cha-benh-hiem-ngheo-be-8-tuoi-mo-uoc-duoc-cung-cha-an-mot-mieng-thit.jpg\" style=\"margin: 0px auto; padding: 0px; outline: 0px; display: inline; max-width: 100%; border: 0px;\" type=\"photo\" /></div>\r\n\r\n<div class=\"PhotoCMS_Caption\" style=\"margin: 0px auto; padding: 5px; outline: 0px; max-width: 100%;\">&nbsp;</div>\r\n</div>\r\n\r\n<div class=\"VCSortableInPreviewMode\" style=\"margin: 0px; padding: 0px; outline: 0px; max-width: 100%; font-family: tahoma; color: rgb(0, 0, 0); line-height: 20px; display: inline-block; width: 460px; text-align: center; box-sizing: border-box; background-color: rgb(238, 238, 238);\" type=\"Photo\">\r\n<div style=\"margin: 0px auto; padding: 0px; outline: 0px; max-width: 100%;\"><img alt=\"Ka Lâm Thuyết ngấu nghiến ăn miếng thịt sau hơn 3 tháng chưa biết đến mùi vị của thịt\" id=\"img_292980\" photoid=\"292980\" rel=\"https://dantri4.vcmedia.vn/96a1faf49c/2016/03/27/hinh6-1459082271306.jpg\" src=\"https://dantri4.vcmedia.vn/k:96a1faf49c/2016/03/27/hinh6-1459082271306/me-chet-cha-benh-hiem-ngheo-be-8-tuoi-mo-uoc-duoc-cung-cha-an-mot-mieng-thit.jpg\" style=\"margin: 0px auto; padding: 0px; outline: 0px; display: inline; max-width: 100%; border: 0px;\" type=\"photo\" /></div>\r\n\r\n<div class=\"PhotoCMS_Caption\" style=\"margin: 0px auto; padding: 5px; outline: 0px; max-width: 100%;\">Ka L&acirc;m Thuyết ngấu nghiến ăn miếng thịt sau hơn 3 th&aacute;ng chưa biết đến m&ugrave;i vị của thịt</div>\r\n</div>\r\n\r\n<p style=\"margin: 9.1875px 0px; padding: 0px; outline: 0px; max-width: 100%; font-family: &quot;Times New Roman&quot;; font-size: 16px; line-height: 20px; color: rgb(0, 0, 0);\">D&ugrave; sức khỏe c&agrave;ng yếu v&agrave; kh&ocirc;ng thể đi lại, nhưng anh Ka Thơ vẫn gắng gượng cầm cự. Nghị lực để anh sống b&acirc;y giờ l&agrave; đứa con thơ, anh Ka Thơ vẫn bắt b&eacute; Thuyết phải đến trường học con chữ để sau n&agrave;y đỡ khổ. &ldquo;Lỡ t&ocirc;i chết, điều kh&ocirc;ng an l&ograve;ng nhất l&agrave; đứa con c&ograve;n qu&aacute; nhỏ, n&oacute; kh&ocirc;ng biết nương tựa v&agrave;o ai? Ngay cả việc con th&egrave;m ăn một miếng thịt m&agrave; t&ocirc;i cũng kh&ocirc;ng lo được th&igrave; n&oacute;i g&igrave; đến việc lo cho tương lai của n&oacute;&rdquo; &ndash; Anh Ka Thơ ứa nước mắt n&oacute;i.</p>\r\n\r\n<p style=\"margin: 9.1875px 0px; padding: 0px; outline: 0px; max-width: 100%; font-family: &quot;Times New Roman&quot;; font-size: 16px; line-height: 20px; color: rgb(0, 0, 0);\">Chị Vũ Thị &Acirc;n (48 tuổi, h&agrave;ng x&oacute;m của anh Ka Thơ) chi sẻ: &ldquo;T&ocirc;i ở gần đ&acirc;y t&ocirc;i biết, bố con Ka Thơ rất khổ, miếng cơm ăn cũng chẳng c&oacute;, b&agrave; con thương t&igrave;nh th&igrave; nấu th&ecirc;m cơm cho hai bố con ăn k&eacute;, ng&agrave;y n&agrave;o con b&eacute; Thuyết cũng phải đi xin cơm quanh x&oacute;m, n&oacute; 8 tuổi rồi m&agrave; nh&igrave;n c&oacute; ch&uacute;t x&iacute;u. Mọi người ở đ&acirc;y ai cũng kh&oacute; khăn n&ecirc;n chẳng gi&uacute;p g&igrave; được l&acirc;u d&agrave;i&rdquo;.</p>\r\n\r\n<p style=\"margin: 9.1875px 0px; padding: 0px; outline: 0px; max-width: 100%; font-family: &quot;Times New Roman&quot;; font-size: 16px; line-height: 20px; color: rgb(0, 0, 0);\">&Ocirc;ng L&ecirc; Ngọc Ch&aacute;nh, Chủ tịch UBND x&atilde;&nbsp;x&aacute;c nhận, ho&agrave;n cảnh của gia đ&igrave;nh anh Ka Thơ rất kh&oacute; khăn v&agrave; đang rơi v&agrave;o t&igrave;nh trạng bi kịch. Ch&iacute;nh quyền địa phương, hội chữ thập đỏ cũng đ&atilde; chung tay gi&uacute;p đỡ nhưng cũng chỉ hỗ trợ được phần n&agrave;o. Thay mặt người đứng đầu ch&iacute;nh quyền x&atilde;, &ocirc;ng Ch&aacute;nh mong c&aacute;c mạnh thường qu&acirc;n, nh&agrave; hảo t&acirc;m chung tay gi&uacute;p sức cho bố con anh Ka Thơ qua cơn khốn kh&oacute;.</p>\r\n\r\n<div class=\"VCSortableInPreviewMode\" style=\"margin: 0px; padding: 0px; outline: 0px; max-width: 100%; font-family: tahoma; color: rgb(0, 0, 0); line-height: 20px; display: inline-block; width: 460px; text-align: center; box-sizing: border-box; background-color: rgb(238, 238, 238);\" type=\"Photo\">\r\n<div style=\"margin: 0px auto; padding: 0px; outline: 0px; max-width: 100%;\"><img alt=\"\" id=\"img_292981\" photoid=\"292981\" rel=\"https://dantri4.vcmedia.vn/96a1faf49c/2016/03/27/hinh3-1459082271338.jpg\" src=\"https://dantri4.vcmedia.vn/k:96a1faf49c/2016/03/27/hinh3-1459082271338/me-chet-cha-benh-hiem-ngheo-be-8-tuoi-mo-uoc-duoc-cung-cha-an-mot-mieng-thit.jpg\" style=\"margin: 0px auto; padding: 0px; outline: 0px; display: inline; max-width: 100%; border: 0px;\" type=\"photo\" /></div>\r\n\r\n<div class=\"PhotoCMS_Caption\" style=\"margin: 0px auto; padding: 5px; outline: 0px; max-width: 100%;\">&nbsp;</div>\r\n</div>\r\n\r\n<div class=\"VCSortableInPreviewMode\" style=\"margin: 0px; padding: 0px; outline: 0px; max-width: 100%; font-family: tahoma; color: rgb(0, 0, 0); line-height: 20px; display: inline-block; width: 460px; text-align: center; box-sizing: border-box; background-color: rgb(238, 238, 238);\" type=\"Photo\">\r\n<div style=\"margin: 0px auto; padding: 0px; outline: 0px; max-width: 100%;\"><img alt=\"Công việc đi xin cơm, chăm cha đã trở nên quen thuộc với Ka Lâm Thuyết\" id=\"img_292982\" photoid=\"292982\" rel=\"https://dantri4.vcmedia.vn/96a1faf49c/2016/03/27/hinh7-1459082271316.jpg\" src=\"https://dantri4.vcmedia.vn/k:96a1faf49c/2016/03/27/hinh7-1459082271316/me-chet-cha-benh-hiem-ngheo-be-8-tuoi-mo-uoc-duoc-cung-cha-an-mot-mieng-thit.jpg\" style=\"margin: 0px auto; padding: 0px; outline: 0px; display: inline; max-width: 100%; border: 0px;\" type=\"photo\" /></div>\r\n\r\n<div class=\"PhotoCMS_Caption\" style=\"margin: 0px auto; padding: 5px; outline: 0px; max-width: 100%;\">C&ocirc;ng việc đi xin cơm, chăm cha đ&atilde; trở n&ecirc;n quen thuộc với Ka L&acirc;m Thuyết</div>\r\n</div>\r\n\r\n<p style=\"margin: 9.1875px 0px; padding: 0px; outline: 0px; max-width: 100%; font-family: &quot;Times New Roman&quot;; font-size: 16px; line-height: 20px; color: rgb(0, 0, 0);\">Để chia sẻ kh&oacute; khăn trước mắt, PV D&acirc;n tr&iacute; đ&atilde; gửi tặng 30kg gạo, &iacute;t tiền để cha con anh Ka Thơ mua đồ ăn &ldquo;cầm cự&rdquo; qua ng&agrave;y v&agrave; chiếc điện thoại &quot;c&ugrave;i bắp&quot; để li&ecirc;n lạc. Một miếng thịt nhỏ được mua về v&agrave; luộc &ldquo;khẩn cấp&rdquo; cho b&eacute; Ka Thơ. Đứa trẻ nh&igrave;n thấy miếng thịt nhưng kh&ocirc;ng d&aacute;m đến gần ăn, khi được cha cho ph&eacute;p, Ka L&acirc;m Thuyết l&eacute;n đến b&ecirc;n, cầm ăn ngấu nghiến, đ&acirc;y l&agrave; miếng thịt đầu ti&ecirc;n sau hơn 3 th&aacute;ng đứa trẻ n&agrave;y mới biết đến m&ugrave;i vị.</p>\r\n',0,0,0,0,10,NULL,0,0,0,'VND',0,NULL,16,NULL,1472172217,1474876157,0,1467331256,1472785256,1,7,NULL,NULL,1472172512),(24,NULL,'Phát động ủng hộ xây dựng trường học cho trẻ em Lũng Cú - Hà Giang',NULL,'Mong muốn của chúng tôi là xây dựng một ngôi trường khang trang để các em học sinh có một nơi học tập ổn định, UBND xã Lũng Cú đã cấp cho nhà trường một mảnh đất mới nhưng điều kiện thiếu thốn và eo hẹp khiến cho mong ước ấy vẫn còn xa xôi.','57c48400e43071472496640.jpg','CP000024',1,NULL,NULL,'<p style=\"border: 0px; font-family: sans-serif; font-size: 1.1em; margin: 8px 0px; outline: 0px; padding: 0px; vertical-align: baseline; text-align: justify; line-height: 1.5em; color: rgb(0, 0, 0);\"><span mce_style=\"font-size: medium;\" style=\"border: 0px; font-family: inherit; font-size: medium; font-style: inherit; font-weight: inherit; margin: 0px; outline: 0px; padding: 0px; vertical-align: baseline;\"><span mce_style=\"font-size: medium;\" style=\"border: 0px; font-family: inherit; font-style: inherit; font-weight: inherit; margin: 0px; outline: 0px; padding: 0px; vertical-align: baseline;\"><span mce_style=\"font-family: arial, helvetica, sans-serif;\" style=\"border: 0px; font-family: arial, helvetica, sans-serif; font-size: 16px; font-style: inherit; font-weight: inherit; margin: 0px; outline: 0px; padding: 0px; vertical-align: baseline;\">X&atilde; Lũng C&uacute;&nbsp;(huyện Đồng Văn, tỉnh H&agrave; Giang) được v&iacute; như một &ldquo;ốc đảo&rdquo; nằm giữa rừng nguy&ecirc;n sinh hoang d&atilde;. Người d&acirc;n nơi đ&acirc;y chủ yếu l&agrave; người d&acirc;n tộc M&ocirc;ng. Đ&acirc;y l&agrave; x&atilde; xa x&ocirc;i v&agrave; đặc biệt kh&oacute; khăn, nhất l&agrave; đối với việc học tập của c&aacute;c em học sinh.</span></span></span></p>\r\n\r\n<p style=\"border: 0px; font-family: sans-serif; font-size: 1.1em; margin: 8px 0px; outline: 0px; padding: 0px; vertical-align: baseline; text-align: justify; line-height: 1.5em; color: rgb(0, 0, 0);\"><span mce_style=\"font-size: medium;\" style=\"border: 0px; font-family: inherit; font-size: medium; font-style: inherit; font-weight: inherit; margin: 0px; outline: 0px; padding: 0px; vertical-align: baseline;\"><span mce_style=\"font-size: medium;\" style=\"border: 0px; font-family: inherit; font-style: inherit; font-weight: inherit; margin: 0px; outline: 0px; padding: 0px; vertical-align: baseline;\"><span mce_style=\"f